  transform: translateX(calc( var(--main-cover-width) + var(--cover-padding) + 4 * (var(--other-cover-width) + var(--cover-padding)) ));
}
.fresh-home-video-slides .fresh-home-video-slides-cover:nth-child(8) {
  transform: translateX(calc( var(--main-cover-width) + var(--cover-padding) + 5 * (var(--other-cover-width) + var(--cover-padding)) ));
}
.fresh-home-video-slides .fresh-home-video-slides-cover:nth-child(9) {
