  font-size: 12px;
  color: #000;
  background-color: #fff;
}
body.dark .be-live-list {
  background-color: #444;
  color: #eee;
}
.be-live-list-header {
  display: flex;
  align-items: center;
  justify-content: space-between;
}
.be-live-list-title {
  display: flex;
  align-items: center;
  gap: 4px;
  font-size: 14px;
}
.be-live-list-count {
  opacity: 0.5;
  font-size: 12px;
}
.be-live-list-actions {
  display: flex;
  align-items: center;
  gap: 4px;
}
.be-live-list-refresh {
  cursor: pointer;
  padding: 2px;
}
.be-live-list-refresh .be-icon {
  opacity: 0.5;
  transition: 0.35s ease-out;
}
.be-live-list-refresh:hover .be-icon {
  transform: rotate(360deg);
  opacity: 1;
  color: var(--theme-color);
}
.be-live-list-more {
  opacity: 0.5;
  padding: 2px;
  cursor: pointer;
  display: flex;
  align-items: center;
}
.be-live-list-more:hover {
  opacity: 1;
  color: var(--theme-color);
}
.be-live-list-search {
  display: flex;
  align-items: center;
  gap: 8px;
}
.be-live-list-search .be-icon {
  opacity: 0.6;
}
.be-live-list-search .be-textbox {
  flex-grow: 1;
}
.be-live-list-content {
  overflow: auto;
  scrollbar-width: none !important;
  overscroll-behavior: contain;
  display: flex;
  align-items: stretch;
  flex-direction: column;
  gap: 8px;
  max-height: calc(100vh - 358px);
}
.be-live-list-content::-webkit-scrollbar {
  height: 0 !important;
  width: 0 !important;
}
body.enable-feeds-filter .be-live-list-content {
  max-height: calc(100vh - 414px);
}
body.feeds-filter-side-block-profile .be-live-list-content {
  max-height: calc(100vh - 218px);
}
.be-live-list-item {
  display: flex;
  align-items: center;
  gap: 8px;
  cursor: pointer;
}
.be-live-list-item-avatar {
  overflow: hidden;
  border-radius: 50%;
  display: flex;
  border: 1px solid #8884;
}
.be-live-list-item-info {
  display: flex;
  align-items: stretch;
  flex-direction: column;
  gap: 2px;
  flex-grow: 1;
  width: 0;
}
.be-live-list-item-title, .be-live-list-item-user {
  overflow: hidden;
  text-overflow: ellipsis;
  white-space: nowrap;
}
.be-live-list-item-title {
  font-size: 14px;
  transition: 0.2s ease-out;
}
.be-live-list-item-user {
  opacity: 0.6;
}
.be-live-list-item:hover .be-live-list-item-title {
  color: var(--theme-color);
