  align-items: center;
}
.bb-comment .comment-list .list-item .info .floor {
  opacity: 0.7;
  order: 1;
}
.bb-comment .comment-list .list-item .info .reply {
  order: 2;
}
.bb-comment .comment-list .list-item .info .reply-tags {
  order: 3;
  display: flex !important;
  margin: 0 !important;
}
.bb-comment .comment-list .list-item .info .reply-tags span {
  margin: 0 4px 0 0 !important;
  font-size: 12px !important;
  line-height: normal !important;
  display: flex !important;
  height: auto !important;
  padding: 2px 6px !important;
}
.bb-comment .comment-list .list-item .info .operation {
