  display: flex;
  align-items: center;
  gap: 0;
  border-bottom: 1px solid rgba(136, 136, 136, 0.1333333333);
  padding: 6px 0;
  margin: 0 6px;
}
.download-video-panel-header .title {
  font-size: 16px;
  font-weight: bold;
  flex-grow: 1;
  margin: 0 8px;
}
.download-video-panel-header .be-button {
  padding: 4px;
}
.download-video-panel-content {
  overflow: auto;
  scrollbar-width: none !important;
  overscroll-behavior: contain;
  display: flex;
  align-items: stretch;
  flex-direction: column;
  gap: 0;
  flex: 1 0 0;
  padding: 12px 6px;
  align-items: flex-start;
}
.download-video-panel-content::-webkit-scrollbar {
  height: 0 !important;
  width: 0 !important;
}
.download-video-panel-content > :not(:first-child) {
  margin-top: 12px;
}
.download-video-panel .download-video-config-item {
  display: flex;
  align-items: center;
  gap: 0;
}
.download-video-panel .download-video-config-item .download-video-config-title {
  margin-right: 8px;
}
.download-video-panel .download-video-config-item.error {
  color: #e57373;
}
.download-video-panel .download-video-config-section {
  align-self: stretch;
}
.download-video-panel .download-video-config-description {
  opacity: 0.5;
  margin-top: 4px;
}
.download-video-panel-footer {
  display: flex;
  align-items: center;
  gap: 0;
  border-top: 1px solid rgba(136, 136, 136, 0.1333333333);
  padding: 6px 0;
  margin: 0 6px;
  justify-content: center;
}
.download-video-panel .run-download {
  font-size: 13px;
  padding: 6px 12px;
