  height: var(--main-cover-height);
}
.fresh-home-video-slides .fresh-home-video-slides-cover:nth-child(3) {
  transform: translateX(calc( var(--main-cover-width) + var(--cover-padding) + 0 * (var(--other-cover-width) + var(--cover-padding)) ));
}
.fresh-home-video-slides .fresh-home-video-slides-cover:nth-child(4) {
