  bottom: 0;
  height: 40px;
  width: 100%;
  font-size: 14px;
  display: flex;
  align-items: center;
  justify-content: center;
  background-color: white;
  color: #99a2aa;
  cursor: pointer;
  transition: all 0.2s ease-out;
  z-index: 110;
  border-radius: 4px;
