}
.fresh-home-video-slides .fresh-home-video-slides-main-actions {
  display: flex;
  align-items: center;
  gap: 8px;
  width: var(--other-cover-width);
  flex-wrap: wrap;
  position: absolute;
  right: 0;
  top: 0;
}
.fresh-home-video-slides .fresh-home-video-slides-main-description {
  display: flex;
  align-items: stretch;
  flex-direction: column;
  overflow: auto;
  scrollbar-width: none !important;
  overscroll-behavior: contain;
  font-size: 13px;
  line-height: 1.5;
  opacity: 0.75;
  flex: 1;
  padding: 2px;
}
.fresh-home-video-slides .fresh-home-video-slides-main-description::-webkit-scrollbar {
  height: 0 !important;
  width: 0 !important;
}
.fresh-home-video-slides .fresh-home-video-slides-main-description .description-text {
  height: 0;
  white-space: pre-wrap;
  word-break: break-word;
}
.fresh-home-video-slides .fresh-home-video-slides-actions {
  display: flex;
  align-items: stretch;
  gap: 8px;
  align-items: flex-end;
}
.fresh-home-video-slides .fresh-home-video-slides-actions .be-button .content-container {
  opacity: 0.8;
}
.fresh-home-video-slides .fresh-home-video-slides-covers {
  position: absolute;
  top: 0;
  left: 0;
  z-index: 1;
}
.fresh-home-video-slides .fresh-home-video-slides-cover {
  background-color: #fff;
  color: black;
  border-radius: 12px;
  border: 1px solid rgba(136, 136, 136, 0.1333333333);
  box-sizing: border-box;
  box-shadow: 0 4px 12px 0 rgba(0, 0, 0, 0.05);
  position: absolute;
  box-shadow: none;
  overflow: hidden;
  top: var(--main-padding-y);
  left: var(--main-padding-x);
  width: var(--other-cover-width);
  height: var(--other-cover-height);
  transition: 0.4s cubic-bezier(0.22, 0.61, 0.36, 1);
}
body.dark .fresh-home-video-slides .fresh-home-video-slides-cover {
  box-shadow: 0 4px 12px 0 rgba(0, 0, 0, 0.2);
}
body.dark .fresh-home-video-slides .fresh-home-video-slides-cover {
  background-color: #282828;
  color: #eee;
}
.fresh-home-video-slides .fresh-home-video-slides-cover img {
  transition: 0.2s ease-out;
  width: 100%;
  height: 100%;
}
.fresh-home-video-slides .fresh-home-video-slides-cover:hover img {
  transform: scale(1.05);
}
.fresh-home-video-slides .fresh-home-video-slides-cover:nth-child(1) {
  opacity: 0;
  transform: translateX(calc(0px - var(--other-cover-width) - var(--cover-padding)));
}
.fresh-home-video-slides .fresh-home-video-slides-cover:nth-child(2) {
  width: var(--main-cover-width);
  height: var(--main-cover-height);
}
.fresh-home-video-slides .fresh-home-video-slides-cover:nth-child(3) {
  transform: translateX(calc( var(--main-cover-width) + var(--cover-padding) + 0 * (var(--other-cover-width) + var(--cover-padding)) ));
}
.fresh-home-video-slides .fresh-home-video-slides-cover:nth-child(4) {
  transform: translateX(calc( var(--main-cover-width) + var(--cover-padding) + 1 * (var(--other-cover-width) + var(--cover-padding)) ));
}
.fresh-home-video-slides .fresh-home-video-slides-cover:nth-child(5) {
  transform: translateX(calc( var(--main-cover-width) + var(--cover-padding) + 2 * (var(--other-cover-width) + var(--cover-padding)) ));
}
.fresh-home-video-slides .fresh-home-video-slides-cover:nth-child(6) {
  transform: translateX(calc( var(--main-cover-width) + var(--cover-padding) + 3 * (var(--other-cover-width) + var(--cover-padding)) ));
}
.fresh-home-video-slides .fresh-home-video-slides-cover:nth-child(7) {
  transform: translateX(calc( var(--main-cover-width) + var(--cover-padding) + 4 * (var(--other-cover-width) + var(--cover-padding)) ));
}
.fresh-home-video-slides .fresh-home-video-slides-cover:nth-child(8) {
  transform: translateX(calc( var(--main-cover-width) + var(--cover-padding) + 5 * (var(--other-cover-width) + var(--cover-padding)) ));
}
.fresh-home-video-slides .fresh-home-video-slides-cover:nth-child(9) {
  transform: translateX(calc( var(--main-cover-width) + var(--cover-padding) + 6 * (var(--other-cover-width) + var(--cover-padding)) ));
}
.fresh-home-video-slides .fresh-home-video-slides-cover:nth-child(10) {
  transform: translateX(calc( var(--main-cover-width) + var(--cover-padding) + 7 * (var(--other-cover-width) + var(--cover-padding)) ));
