.bb-comment .comment-send-lite {
  position: sticky !important;
  bottom: 0 !important;
  background: linear-gradient(to top, #fff, rgba(255, 255, 255, 0)) !important;
  pointer-events: none;
  width: calc(100% + 12px) !important;
  margin: 0 0 0 -12px !important;
  padding-left: 97px !important;
}
body.dark .bb-comment .comment-send-lite {
  background: linear-gradient(to top, #222, rgba(255, 255, 255, 0)) !important;
}
.bb-comment .comment-send-lite .comment-emoji-lite {
  background-color: #fff;
}
.bb-comment .comment-send-lite .comment-submit {
  height: 64px !important;
  padding: 4px 15px !important;
  position: relative !important;
  right: 0 !important;
  margin-left: 10px !important;
}
.bb-comment .comment-send-lite .textarea-container .baffle {
  line-height: 65px !important;
}
.bb-comment .comment-send-lite .textarea-container .baffle,
.bb-comment .comment-send-lite .textarea-container .ipt-txt {
  height: 65px !important;
  width: calc(100% - 80px) !important;
}
.bb-comment .comment-send-lite > * {
  pointer-events: initial;
}
.bb-comment .loading-state {
  font-size: 14px !important;
  height: 1.4em !important;
  line-height: 1.4 !important;
  margin: 12px 0 !important;
}
.bb-comment .loading-state + .bottom-page {
  margin: 0 !important;
}
.bb-comment .nameplate,
.bb-comment .comment-header .tabs-order li.on::after,
.bb-comment .true-love,
.bb-comment .medal,
.bb-comment .medal-level,
.bb-comment .reply-notice,
.bb-comment .sailing,
.bb-comment .perfect-reply {
  display: none !important;
}
.bb-comment .comment-send-lite .comment-emoji,
.bb-comment .comment-send .comment-emoji {
  box-shadow: none !important;
}
.bb-comment .comment-send-lite .comment-emoji span,
.bb-comment .comment-send .comment-emoji span {
  opacity: 0.4;
  color: black;
}
.bb-comment .comment-send-lite .comment-emoji .face,
.bb-comment .comment-send .comment-emoji .face {
  transition: all 0.2s ease-out;
  height: 16px !important;
  width: 16px !important;
  background-position: center !important;
  opacity: 0.4;
  display: inline-flex !important;
  background: none !important;
  color: #000;
}
body.dark .bb-comment .comment-send-lite .comment-emoji .face,
body.dark .bb-comment .comment-send .comment-emoji .face {
  color: #eee;
}
.bb-comment .comment-send-lite .comment-emoji .face::before,
.bb-comment .comment-send .comment-emoji .face::before {
  content: "\f01f5";
  display: inline-block;
  font: normal normal normal 24px/1 "Material Design Icons";
  font-size: 16px;
  line-height: 1;
  width: 16px;
  height: 16px;
}
.bb-comment .comment-send-lite .comment-emoji .text,
.bb-comment .comment-send .comment-emoji .text {
  transition: all 0.2s ease-out;
}
body.dark .bb-comment .comment-send-lite .comment-emoji .text,
body.dark .bb-comment .comment-send .comment-emoji .text {
  filter: brightness(0) invert(1) !important;
}
.bb-comment .comment-send-lite .comment-emoji.open span,
.bb-comment .comment-send-lite .comment-emoji.open .face, .bb-comment .comment-send-lite .comment-emoji:hover span,
.bb-comment .comment-send-lite .comment-emoji:hover .face,
.bb-comment .comment-send .comment-emoji.open span,
.bb-comment .comment-send .comment-emoji.open .face,
.bb-comment .comment-send .comment-emoji:hover span,
.bb-comment .comment-send .comment-emoji:hover .face {
  opacity: 1;
}
.bb-comment .comment-list .list-item {
  position: relative;
}
.bb-comment .comment-list .list-item .info {
  margin-top: 0 !important;
  display: flex;
  align-items: center;
}
.bb-comment .comment-list .list-item .info .floor {
  opacity: 0.7;
  order: 1;
}
.bb-comment .comment-list .list-item .info .reply {
  order: 2;
}
.bb-comment .comment-list .list-item .info .reply-tags {
  order: 3;
  display: flex !important;
  margin: 0 !important;
}
.bb-comment .comment-list .list-item .info .reply-tags span {
  margin: 0 4px 0 0 !important;
  font-size: 12px !important;
  line-height: normal !important;
  display: flex !important;
  height: auto !important;
  padding: 2px 6px !important;
}
.bb-comment .comment-list .list-item .info .operation {
  order: 4;
  flex-grow: 1;
  display: flex !important;
  justify-content: flex-end;
  margin: 0 !important;
  padding: 3px !important;
}
.bb-comment .comment-list .list-item .info > * {
  display: flex;
  align-items: center;
